    private void BCargarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BCargarActionPerformed
        
        if (C.getText().isEmpty() && F.getText().isEmpty()){
            JOptionPane.showMessageDialog(null, "Debe ingresar uno de los dos datos");
        } else {
            if (C.getText().isEmpty() && !F.getText().isEmpty()) {
               double temp = Double.parseDouble(F.getText());
               
                C.setText(String.valueOf(Math.round(temp-32)*4.5));
            } else {
                if (!C.getText().isEmpty() && F.getText().isEmpty()) {
                double temp = Double.parseDouble(C.getText());
                
                F.setText(String.valueOf(Math.round(temp*4.5)+32));
                } else {
                     if (!C.getText().isEmpty() && !F.getText().isEmpty()){
                     JOptionPane.showMessageDialog(null, "No pueden estar ingresados ambos datos");
        }
                }
            }
        }
    }//GEN-LAST:event_BCargarActionPerformed

