    private void CKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_CKeyTyped
        //Funcion para que solo deje ingresar numeros del 0 a 9 y el punto (.)
        
        //toma el valor tipedo
        int key = evt.getKeyChar();
        //Los valores 46 (punto) y del 48 al 57 (numeros) son los valores que toman de la tabla ascii
        boolean numero = key == 46 || key >= 48 && key <= 57;
        
        //Si numero es false tipea los valores que le permitimos
        if (!numero) {
            evt.consume();
        }
    }//GEN-LAST:event_CKeyTyped

